CREATE TABLE hash(hashId INT PRIMARY KEY, filename VARCHAR(255) NOT NULL, startLine INT NOT NULL, endLine INT NOT NULL, hashValue INT NOT NULL, artefactId INT NOT NULL);
CREATE TABLE artefact(artefactId INT PRIMARY KEY, name VARCHAR(255) NOT NULL, date VARCHAR(255), sources BLOB NOT NULL, classes BLOB NOT NULL);
CREATE TABLE clone(cloneId INT PRIMARY KEY, artefactId INT NOT NULL, hashId INT NOT NULL, FOREIGN KEY (artefactId) REFERENCES ARTEFACT(ARTEFACTID), FOREIGN KEY (HASHID) REFERENCES HASH(HASHID), UNIQUE (artefactId, hashId));
CREATE TABLE score(scoreId INT PRIMARY KEY, firstArtefact INT NOT NULL , secondArtefact INT NOT NULL, FOREIGN KEY (firstArtefact) REFERENCES ARTEFACT(ARTEFACTID), FOREIGN KEY (secondArtefact) REFERENCES ARTEFACT(ARTEFACTID), UNIQUE(firstArtefact, secondArtefact), CHECK (firstArtefact != secondArtefact));